parameters.jsonBody:
={
  "post": {
    "target": {
      "targetType": "tiktok",
      "isYourBrand": false,
      "disabledDuet": false,
      "privacyLevel": "PUBLIC_TO_EVERYONE",
      "isAiGenerated": true,
      "disabledStitch": false,
      "disabledComments": false,
      "isBrandedContent": false
    },
    "content": {
      "text": "{{ $('Idea Agent').item.json.output.caption }}",
      "platform": "tiktok",
      "mediaUrls": ["{{ $json.url }}"]
    },
    "accountId": "YOUR ACCOUNT ID"
  }
}

parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "object": {
      "type": "string",
      "description": "A new object name in the format 'glass [object]', all lowercase with no punctuation."
    },
    "caption": {
      "type": "string",
      "description": "A caption in the format 'glass [object] ASMR', matching the object field exactly."
    }
  },
  "required": ["object", "caption"]
}
