parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "object": {
      "type": "string",
      "description": "A new object name in the format 'glass [object]', all lowercase with no punctuation."
    },
    "caption": {
      "type": "string",
      "description": "A caption in the format 'glass [object] ASMR', matching the object field exactly."
    }
  },
  "required": ["object", "caption"]
}
