// Extract kilometers from OCR analysis
const analysis = $input.item.json.content || $input.item.json.text || '';

// Common patterns for odometer readings
const patterns = [
  /(?:odometer|odo|mileage|km|kilometers?)\s*:?\s*([0-9,\.\s]+)\s*(?:km|kilometers?)?/i,
  /([0-9]{1,3}(?:[,\.]?[0-9]{3})*(?:\.[0-9]+)?)\s*(?:km|kilometers?)/i,
  /total\s*(?:distance|km|kilometers?)\s*:?\s*([0-9,\.\s]+)/i,
  /([0-9]{3,6})\s*(?:km|kilometers?)/i
];

let extractedKm = null;
let confidence = 'low';

// Try each pattern
for (const pattern of patterns) {
  const match = analysis.match(pattern);
  if (match) {
    let kmValue = match[1].replace(/[,\s]/g, '').replace(/\.$/, '');
    const numValue = parseFloat(kmValue);

    // Validate reasonable odometer reading (between 0 and 999,999 km)
    if (!isNaN(numValue) && numValue >= 0 && numValue <= 999999) {
      extractedKm = Math.round(numValue);
      confidence = 'high';
      break;
    }
  }
}

// Fallback: extract any number that looks like an odometer reading
if (!extractedKm) {
  const numbers = analysis.match(/\b([0-9]{4,6})\b/g);
  if (numbers) {
    for (const num of numbers) {
      const value = parseInt(num);
      if (value >= 1000 && value <= 999999) {
        extractedKm = value;
        confidence = 'medium';
        break;
      }
    }
  }
}

return {
  originalAnalysis: analysis,
  extractedKilometers: extractedKm,
  confidence: confidence,
  chatId: $input.item.json.message?.chat?.id,
  messageId: $input.item.json.message?.message_id,
  formattedKm: extractedKm ? extractedKm.toLocaleString() : null
};

